#!/usr/bin/env python3

def find_start(puzzle_input: str, unique: int) -> int:
  for i in range(unique-1, len(puzzle_input)):
    if len(set(puzzle_input[i-unique:i])) == unique:
      return i

puzzle_input = ''
print(f"Start of packet: {find_start(puzzle_input, 4)}")

puzzle_input = ''
print(f"Start of message: {find_start(puzzle_input, 14)}")
